import dynamicTable from './src/dynamic-table.vue';
import repository from './src/components/index.js';
import enumerator from './src/enumerator.js';
import eventBus from './src/eventBus.js';

const tableGenerator = {
    enumerator,
    repository,
    install (Vue, options) {
        Vue.component('tableGenerator', dynamicTable);
        Vue.prototype.$tableGenerator = eventBus(Vue);
    }
};

//auto install
if (typeof window !== 'undefined' && window.Vue) {
    window.Vue.use(tableGenerator);
}

export default tableGenerator;
